<table class="cls-table-gallery wpscls-with-content">			<?php if ( have_posts() ) while ( have_posts() ) {							the_post(); 							global $post;														$strGalleryClass	=	'';							$strPostContent	=	'';							if (trim($post->post_content))							{								$strGalleryClass	.=	' wpscls-with-content';								$strPostContent	.=	'<h2 class="wpscls-field-title wpscls-'.$post->post_name.'">'. get_the_title() .'</h2>';								$strPostContent	.=	'<div class="wpscls-field-content">';								ob_start();								the_content();								$strPostContent	.= ob_get_contents();								ob_end_clean();								$strPostContent	.=	'</div>';							}													$rows_img	=	0; // $the_query->posts;							$numItemsPerRow	=	100;							$strResultGallery	.= '<tr>';							$strResultGallery	.= $strPostContent ? '<td class="wpscls-post-content">'.$strPostContent.'</td>' : '';															$strResultGallery	.= '</tr>';															if ($strPostContent) echo $strResultGallery;							// break;						}			?></table>